import React from 'react';
import { Box, Paper, Typography } from '@mui/material';
import { MigrationStats, CityStats } from '../types';

interface StatsProps {
    migrationStats: MigrationStats;
    cityStats?: CityStats;
}

const Stats: React.FC<StatsProps> = ({ migrationStats, cityStats }) => {
    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
            <Paper sx={{ p: 2 }}>
                <Typography variant="h6" gutterBottom>
                    Загальна статистика міграції
                </Typography>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
                    <Box sx={{ flex: '1 1 45%' }}>
                        <Typography variant="body1">
                            Всього міграцій: {migrationStats.totalMigrations}
                        </Typography>
                    </Box>
                    <Box sx={{ flex: '1 1 45%' }}>
                        <Typography variant="body1">
                            Середній вік: {migrationStats.averageAge.toFixed(1)} років
                        </Typography>
                    </Box>
                    <Box sx={{ flex: '1 1 45%' }}>
                        <Typography variant="body1">
                            Чоловіків: {migrationStats.maleCount}
                        </Typography>
                    </Box>
                    <Box sx={{ flex: '1 1 45%' }}>
                        <Typography variant="body1">
                            Жінок: {migrationStats.femaleCount}
                        </Typography>
                    </Box>
                </Box>
            </Paper>

            {cityStats && (
                <Paper sx={{ p: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Статистика по місту
                    </Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
                        <Box sx={{ flex: '1 1 45%' }}>
                            <Typography variant="body1">
                                Від'їжджають: {cityStats.outgoingCount}
                            </Typography>
                        </Box>
                        <Box sx={{ flex: '1 1 45%' }}>
                            <Typography variant="body1">
                                Приїжджають: {cityStats.incomingCount}
                            </Typography>
                        </Box>
                        <Box sx={{ flex: '1 1 100%' }}>
                            <Typography variant="body1">
                                Середня відстань: {cityStats.averageDistance.toFixed(1)} км
                            </Typography>
                        </Box>
                    </Box>
                </Paper>
            )}
        </Box>
    );
};

export default Stats;
