import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, CircleMarker, Polyline, Popup } from 'react-leaflet';
import { LatLngTuple } from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { City, MigrationFlow } from '../types';

interface MapProps {
    cities: City[];
    migrationFlows: MigrationFlow[];
    selectedCity?: string;
    onCitySelect: (cityName: string) => void;
}

const Map: React.FC<MapProps> = ({ cities, migrationFlows, selectedCity, onCitySelect }) => {
    const [center, setCenter] = useState<LatLngTuple>([48.379433, 31.165581]); // Центр України
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        if (selectedCity) {
            const city = cities.find(c => c.name === selectedCity);
            if (city) {
                setCenter([city.lat, city.lng]);
            } else {
                setError('Місто не знайдено');
            }
        }
    }, [selectedCity, cities]);

    if (error) {
        return (
            <div className="error">
                <h2>Помилка</h2>
                <p>{error}</p>
            </div>
        );
    }

    return (
        <MapContainer
            center={center}
            zoom={7}
            style={{ height: '100vh', width: '100%' }}
        >
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            {cities.map(city => (
                <CircleMarker
                    key={city.name}
                    center={[city.lat, city.lng] as LatLngTuple}
                    radius={Math.sqrt(city.population) / 100}
                    fillColor={city.name === selectedCity ? '#e74c3c' : '#3498db'}
                    color="#fff"
                    weight={2}
                    eventHandlers={{
                        click: () => onCitySelect(city.name)
                    }}
                >
                    <Popup>
                        <div>
                            <h3>{city.name}</h3>
                            <p>Населення: {city.population.toLocaleString()}</p>
                            <p>Регіон: {city.region}</p>
                        </div>
                    </Popup>
                </CircleMarker>
            ))}
            {migrationFlows.map((flow, index) => {
                const fromCity = cities.find(c => c.name === flow.from_city);
                const toCity = cities.find(c => c.name === flow.to_city);

                if (!fromCity || !toCity) return null;

                return (
                    <Polyline
                        key={index}
                        positions={[
                            [fromCity.lat, fromCity.lng],
                            [toCity.lat, toCity.lng]
                        ]}
                        color="#2ecc71"
                        weight={Math.min(flow.count / 100, 5)}
                        opacity={0.6}
                    >
                        <Popup>
                            <div>
                                <h3>Міграційний потік</h3>
                                <p>Від: {flow.from_city}</p>
                                <p>До: {flow.to_city}</p>
                                <p>Кількість: {flow.count}</p>
                                <p>Середній вік: {flow.average_age}</p>
                                <p>Середня тривалість: {flow.average_duration} днів</p>
                                <p>Відстань: {flow.total_distance} км</p>
                            </div>
                        </Popup>
                    </Polyline>
                );
            })}
        </MapContainer>
    );
};

export default Map;
